import axios from "axios";
import React, { useEffect, useState } from "react";
import request from "../utlis/request";

const Main = () => {
  const [movies, setMovies] = useState([]);
  const movie = movies[Math.floor(Math.random() * movies.length)];

  useEffect(() => {
    axios.get(request.requestPopular).then((res) => {
      setMovies(res.data.results);
    });
  }, []);

 const turncateString =(str,num)=>{
  if(str?.length > num){
    return str.slice(0,num)
  }
 }

  return (
    <div className="w-full h-[550px] text-white">
      <div className="w-full h-full">
        <div className="bg-gradient-to-r from-black absolute h-[550px] w-full"></div>
        <img
          className="w-full h-full object-cover "
          src={`https://image.tmdb.org/t/p/original/${movie?.backdrop_path}`}
          alt={movie?.title}
        />
        <div className="absolute w-full top-[20%] p-4 md:p-8">
          <h1 className="text-3xl text-white font-bold  md:text-5xl">
            {movie?.title}
          </h1>
          <div className=" my-6">
            <button className="border text-black  bg-gray-300 px-5 py-2">
              Play
            </button>
            <button className="border text-white  px-5 py-2 ml-4">
              Watch Later
            </button>
          </div>
          <p className="text-gray-400 text-sm">
            Released: {movie?.release_date}
          </p>
          <p className="w-full md:max-w-[70%] lg:max-w-[50%] xl:max-w-[35%] text-gray-200">
            {movie?.overview}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Main;
